    {
      name: "firstname",
      inputName: "firstname",
      required: true,
      requiredError
    },
    { name: "lastname", inputName: "lastname", required: true, requiredError },
    {
      name: "email",
      inputName: "email",
      required: true,
      requiredError,
      unique: true,
      uniqueError,
      validate: isEmailValid,
      validateError
    },
    {
      name: "password",
      inputName: "password",
      required: true,
      requiredError,
      validate: isPasswordValid,
      validateError
    },
    {
      name: "phonenumber",
      inputName: "phonenumber",
      required: true,
      requiredError,
      validate:isPhoneNumberValid,
      validateError
    }
  ]
};
