import React,{useState,useEffect} from 'react';
import axios from 'axios';
import edit from './edit';
export default function(){
    const[user,setUser]=useState([]);
    const init=()=>{
        edit.getAll()
        .then(response=>{
            console.log('Getting Files Successfully',response.data);
            setUser(response.data);
        })
        .catch(error=>{
            console.log("Somthing Went Wrong",error);
        })
    }
    // useEffect(()=>{
    //     init();
//     axios({
//         url:"http://localhost:8080/api/csv/tutorials",
//         method:'GET',
//         headers:{
//             // authorization:'token'
//         }
//     }).then(response=>{
//         console.log("Getting User Details Successfully",response.data);
//         setUser(response.data);
//     })
//    . catch(error =>{
//         console.log("Something went Wrong",error);
//     })
//   },[]);
  const files={id,email,password,phonenumber,firstname,lastname}
  const {id} = useParams();
  if(id){
    edit.update(files)
    .then(response=>{
        console.log("Data Updated Succeessfully",response.data);
    })
    .catch(error=>{
        console.log("Somthing Went Wrong",error);
    })
  }
  else{
    edit.create(files)
    .then(response=>{
        console.log("User added Successfully",response.data);
    })
    .catch(error=>{
        console.log("Something went Wrong",error);
    })
  }
  useEffect(()=>{
  if(id){
    edit.get(id)
    .then(files=>{
        setEmail(files.data.email);
        setPassword(files.data.password);
        setPhonenumber(files.data.phonenumber);
        setFirstname(files.data.firstname);
        setLastname(files.data.lastname);
    })
    .catch(error=>{
        console.log("Somthing Went Wrong");
    })
}
  },[])
  const handleDelete=(id)=>{
    console.log('printing id',id);
    edit.update(id)
    .then(response=>{
        console.log("Data Deleted successfully",response.data);
        init();
    })
    .catch(error=>{
        console.log("Somthing Went Wrong",error);
    })
  }
  return(
    <div className='container'>
        <h3>List of Datas</h3>
        <div>
            <table className="table table-bordered table-striped">
            <thead className="thead-dark">
                <tr>
                    {/* <th>ID</th> */}
                    <th>ID</th>
                   
                    <th>Email</th>
                    <th>Password</th>
                    <th>Phone Number</th>
                    <th>Last Name</th>
                    <th>First Name</th>
    
                </tr>
                </thead>
                <tbody>
                {
                    user.map(users=>(
                        <tr key={users.id}>
                            
                            <td>{users.id}</td>

                            <td>{users.email}</td>
                            <td>{users.password}</td>
                            <td>{users.phonenumber}</td>
                            <td>{users.lastname}</td>
                            <td>{users.firstname}</td>
                            <td>
                            <button className="btn btn-danger ml-2" onClick={() => {
                    handleDelete(users.id);
                  }}>Delete</button>
                            </td>
      
                        </tr>
                    ))
                }
                </tbody>
            </table>
        </div>
    </div>
  )
  

}
