import React,{Component} from "react";
import axios from 'axios';
import CSVFileValidator from "csv-file-validator";
let requiredError = (headerName, rowNumber, columnNumber) => {
    return `<div class="red">${headerName} is required in the <strong>${rowNumber} row</strong> / <strong>${columnNumber} column</strong></div>`;
  };
  let validateError = (headerName, rowNumber, columnNumber) => {
    return `<div class="red">${headerName} is not valid in the <strong>${rowNumber} row</strong> / <strong>${columnNumber} column</strong></div>`;
  };
  let uniqueError = (headerName) => {
    return `<div class="red">${headerName} is not unique</div>`;
  };
  let isEmailValid = function (email) {
    let reqExp = /[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$/;
    return reqExp.test(email);
  };
  let isPasswordValid = function (password) {
    return password.length >= 4;
  };
  let isPhoneNumberValid=function(phonenumber){
    return phonenumber.length ==10;
  }
  let CSVConfig = {
    headers: [
      {
        name: "First Name",
        inputName: "firstName",
        required: true,
        requiredError
      },
      { name: "Last Name", inputName: "lastName", required: true, requiredError },
      {
        name: "Email",
        inputName: "email",
        required: true,
        requiredError,
        unique: true,
        uniqueError,
        validate: isEmailValid,
        validateError
      },
      {
        name: "Password",
        inputName: "password",
        required: true,
        requiredError,
        validate: isPasswordValid,
        validateError
      },
      {
        name: "Phone Number",
        inputName: "phonenumber",
        required: true,
        requiredError,
        validate:isPhoneNumberValid,
        validateError
      }
    ]
  };
class File extends Component{
    constructor(props) {
        super(props);
        this.state = {
          csvData: 0
        };
      }
    state={
        file:null
    }
    handleFile(e){
        CSVFileValidator(e.target.files[0], CSVConfig).then(({csvData}) => {
            csvData:this.state.csvData.inValidMessages.forEach((message) => {
              document.getElementById("invalidMessages").insertAdjacentHTML("beforeend", message);
            });
      
            this.setState({csvData:this.state.csvData.data});
          });
        // let file= CSVFileValidator(e.target.files[0], CSVConfig)
        // this.setState({file:file})
    }
    handleUpload(e){
       let file=this.state.file
       let formdata=new FormData()
       formdata.append('file',file)
        axios({
            url:'http://localhost:8080/api/csv/upload',
            method:'POST',
            headers:{
                authorization:'token'
            },
            data:formdata
        }).then((res)=>{

        })
    }
    render(){
        return(
            <div className="file">
                <h1>The Form</h1>
                <form>
                    <div className="">
                        <label>Select File</label>
                        <input type="file" name="file" onChange={(e)=>this.handleFile(e)}/>
                    
                    </div>
                    <button type="button" onClick={(e)=>this.handleUpload(e)}>upload</button>
                </form>
            </div>
        );
        }
}
export default File;
