import React, { useState } from "react";
import CSVFileValidator from "csv-file-validator";
import { CSVLink } from "react-csv";
import axios from 'axios';
// import "./main.css";

// import "./ValidateCSV.scss";

let requiredError = (headerName, rowNumber, columnNumber) => {
  return `<div class="red">${headerName} is required in the <strong>${rowNumber} row</strong> / <strong>${columnNumber} column</strong></div>`;
};
let validateError = (headerName, rowNumber, columnNumber) => {
  return `<div class="red">${headerName} is not valid in the <strong>${rowNumber} row</strong> / <strong>${columnNumber} column</strong></div>`;
};
let uniqueError = (headerName) => {
  return `<div class="red">${headerName} is not unique</div>`;
};
let isEmailValid = function (email) {
  let reqExp = /[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$/;
  return reqExp.test(email);
};
let isPasswordValid = function (password) {
  return password.length >= 4;
};
let isPhoneNumberValid=function(phonenumber){
  return phonenumber.length ==10;
}
let CSVConfig = {
  headers: [
    {
      name: "First Name",
      inputName: "firstName",
      required: true,
      requiredError
    },
    { name: "Last Name", inputName: "lastName", required: true, requiredError },
    {
      name: "Email",
      inputName: "email",
      required: true,
      requiredError,
      unique: true,
      uniqueError,
      validate: isEmailValid,
      validateError
    },
    {
      name: "Password",
      inputName: "password",
      required: true,
      requiredError,
      validate: isPasswordValid,
      validateError
    },
    {
      name: "Phone Number",
      inputName: "phonenumber",
      required: true,
      requiredError,
      validate:isPhoneNumberValid,
      validateError
    }
  ]
};
let headerCsvDownloadConfig = [
  { label: "First Name", key: "firstName" },
  { label: "Last Name", key: "lastName" },
  { label: "Email", key: "email" },
  { label: "Password", key: "password" },
  { label: "Phone Number", key: "phonenumber" }
];

export default function ValidateCSV() {
  const[firstName,setFirstName]=useState("");
  const[lastName,setLastName]=useState("");
  const[email,setEmail]=useState("");
  const[password,setPassword]=useState("");
  const[phonenumber,setPhoneNumber]=useState("");
  let [csvData, setCsvData] = useState("");

  let handleChange = (e) => {
    CSVFileValidator(e.target.files[0], CSVConfig).then((csvData) => {
      csvData.inValidMessages.forEach((message) => {
        document
          .getElementById("invalidMessages")
          .insertAdjacentHTML("beforeend", message);
      });

      setCsvData(csvData.data);
    });
  };
  var singleUploadForm = document.querySelector('#singleUploadForm');
  var singleFileUploadInput = document.querySelector('#singleFileUploadInput');
  var singleFileUploadError = document.querySelector('#singleFileUploadError');
  var singleFileUploadSuccess = document.querySelector('#singleFileUploadSuccess');
  console.log({ csvData });
  function handleSubmit(){

    let file=this.state.file
    let formdata=new FormData()
    formdata.append('file',file)
     axios({
         url:'http://localhost:8080/api/csv/upload',
         method:'POST',
         headers:{
             authorization:'token'
         },
         data:formdata
     }).then((res)=>{

     })
  }
  singleUploadForm?.addEventListener('submit', function(event){
    var files = singleFileUploadInput.files;
    if(files.length === 0) {
        singleFileUploadError.innerHTML = "Please select a file";
        singleFileUploadError.style.display = "block";
    }
    handleSubmit(files[0]);
    event.preventDefault();
}, true);
  return (
  //   <div className="upload-container">
  //     {/* <div className="input-box>" */}
  //    <div className="validate-csv" > 
  //   <div className="upload-content">
  //               <div className="single-upload">
  //                   <h3>Upload the CSV File </h3>
  //                   <form id="singleUploadForm" name="singleUploadForm" onSubmit={handleSubmit} encType="multipart/form-data">
  //                       <input id="singleFileUploadInput"   type="file" name="file"  onChange={handleChange}  className="file-input" required />
  //                       <button type="submit" className="primary submit-btn">Submit</button>
  //                   </form>
  //                   <div id="invalidMessages" />
  //                   <div className="upload-response">
  //                       <div id="singleFileUploadError"></div>
  //                       <div id="singleFileUploadSuccess"></div>
  //                   </div>
  //               </div>
  //               </div>

  //  </div>


           <div className="upload-container">
           <div className="upload-header">
               <h2>Import the CSV file into Mysql Database using Spring Boot Application</h2>
           </div>
           <div className="upload-content">
               <div className="single-upload">
                   <h3>Upload the CSV File </h3>
                   <form id="singleUploadForm" name="singleUploadForm" onSubmit={handleSubmit}>
                       <input id="singleFileUploadInput" type="file" name="file" className="file-input" onChange={handleChange} required />
                       <button type="submit" className="primary submit-btn">Submit</button>
                   </form>
                    <div class="upload-response">
                       <div id="singleFileUploadError"></div>
                       <div id="singleFileUploadSuccess"></div>
                   </div>
               </div>

           </div>
       </div>


  );
}
