        })
    }
    render(){
        return(
            <div className="file">
                <h1>The Form</h1>
                <form>
                    <div className="">
                        <label>Select File</label>
                        <input type="file" name="file" onChange={(e)=>this.handleFile(e)}/>
                    
                    </div>
                    <button type="button" onClick={(e)=>this.handleUpload(e)}>upload</button>
                </form>
            </div>
        );
        }
}
export default File;
